html {
  height: 100%;
  margin: 0px;
}
body {
  height: 100%;
  text-align: center;
  padding: 0px;
  margin: 0px;
  background: {{$c2}}; /* Old browsers */
  background: -moz-linear-gradient(top, {{$c1}} 0%, {{$c2}} 100%); /* FF3.6+ */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,{{$c1}}), color-stop(100%,{{$c2}})); /* Chrome,Safari4+ */
  background: -webkit-linear-gradient(top, {{$c1}} 0%,{{$c2}} 100%); /* Chrome10+,Safari5.1+ */
  background: -o-linear-gradient(top, {{$c1}} 0%,{{$c2}} 100%); /* Opera 11.10+ */
  background: -ms-linear-gradient(top, {{$c1}} 0%,{{$c2}} 100%); /* IE10+ */
  background: linear-gradient(top, {{$c1}} 0%,{{$c2}} 100%); /* W3C */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='{{$c1}}', endColorstr='{{$c2}}',GradientType=0 ); /* IE6-9 */
  background-repeat: no-repeat;
  background-attachment: fixed;
}
td, th, p, ul,li,div, span {
  font-family: Trykker, "Lucida Grande", Georgia, serif;
  font-size: 12px;
  color: {{$font_colour}};
}
input, textarea, select {
  font-family: Trykker, "Lucida Grande", Georgia, serif;
  font-size: 12px;
}
td, th, p, textarea, ul,li, div, a {
  line-height: 25px;
}
table {
  empty-cells: show;
}
.clear {
  clear: both;
}
a:link, a:visited {
  color: {{$link_colour}};
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}
div.ui-dialog div, div.ui-dialog li, div.ui-dialog span {
  color: #333333;
}

/* page sections */
.ts_page {
  margin: 40px auto;
  position: relative;
  text-align: left;
}
.ts_head_bg {
  height: 105px;
  border-bottom: 1px solid {{$line}};
  position: absolute;
  top: 0px;
  width: 100%;
}
.ts_header {
  height: 70px;
}
.ts_header h1 {
  margin: 20px 20px;
  font-family: Trykker, "Lucida Grande", Georgia, serif;
  font-weight: bold;
  padding: 0px;
  font-size: 30px;
  color: {{$header_colour}};
  {{if $header_shadow == "on"}}text-shadow: 2px 2px 5px #555555;{{/if}}
}
h2 {
  font-size: 21px;
  font-family: Trykker, "Lucida Grande", Georgia, serif;
  color: {{$page_heading}};
}

/* navigation */
#ts_css_nav {
  width: 180px;
  float: left;
  list-style: none;
  padding: 20px;
  margin: 0px;
}
#ts_css_nav li {
  list-style: none;
  margin: 0px 0px 2px;
  color: #666666;
  font-size: 12px;
  line-height: 20px;
  text-align: left;
}
#ts_css_nav div {
  color: {{$nav_next_page}};
}
#ts_css_nav li div, #ts_css_nav li a {
  padding: 5px 0px 5px 12px;
  display: block;
}
ul#ts_css_nav li.completed_page {
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  border: 1px solid {{$nav_prev_page_border}};
}
ul#ts_css_nav li.completed_page div {
  color: {{$nav_prev_page_text}};
}
ul#ts_css_nav li.css_nav_current_page {
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  border: 1px solid #ffffff;
}
ul#ts_css_nav li.css_nav_current_page div {
  background-color: #222222;
  color: white;
  margin: 1px;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}
ul#ts_css_nav li a {
  margin: 1px;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}
ul#ts_css_nav li a:link, ul#ts_css_nav li a:visited {
  text-decoration: none;
  color: {{$nav_prev_page_text}};
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
}
ul#ts_css_nav li a:hover {
  color: white;
  background-color: {{$nav_prev_page_bg_over}};
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}

/* notifications */
.notify {
  border: 1px solid #336699;
  background-color: #ffffee;
  color: #336699;
  padding: 8px;
  width: 400px;
}
.notify li { color: #336699; }
.error {
  font-size: 8pt;
  border: 1px solid #cc0000;
  background-color: #ffffee;
  color: #cc0000;
  padding: 8px;
  width: 550px;
}
.error span {
  color: #cc0000;
  font-weight: bold;
  margin-bottom: 4px;
}

/* forms */
table.table_1 > tbody > tr > td {
  border-bottom: 1px solid {{$fieldset_lines}};
}
table.table_1 > tbody > tr > td.rowN {
  border-bottom: none;
}
.req {
  color: #aa0000;
}
.fb_error {
  background-color: #FFFFCC;
  border: 1px solid #CC0000;
  color: #CC0000;
  margin-bottom: 12px;
  padding: 8px;
}

/* for the code / markup editor */
.editor {
  background-color: white;
  border: 1px solid #999999;
  padding: 3px;
}
.ts_page_content {
  width: 720px;
  float: right;
}
fieldset {
  border: 1px solid {{$fieldset_lines}};
  font-size: 11pt;
  font-weight: bold;
  color: {{$fieldset_colour}};
  margin-bottom: 10px;
  background-color: {{$fieldset_bg}};
}
{{if $fieldset_shadow == "on"}}
fieldset legend {
  text-shadow: 2px 2px 3px #333333;
}
{{/if}}
.ts_continue_button input {
  background-color: #222222;
  color: white;
  padding: 2px 10px;
  border-radius: 3px;
  border: 0px;
  cursor: pointer;
}
.ts_continue_button input:hover {
  background-color: {{$submit_btn_over}};
}
#ts_footer {
  border-top: 1px solid {{$line}};
  padding: 20px;
  color: #222222;
}
.edit_link {
  text-shadow: none;
  margin-left: 12px;
}
.edit_link a {
  color: {{$edit_link_colour}};
  text-decoration: none;
}
.edit_link a:hover {
  text-decoration: underline;
}

#form_builder__edit_link {
  position: absolute;
  right: 5px;
  top: 5px;
  padding: 2px 10px;
  background-color: black;
  border: 1px solid white;
  color: white;
  text-decoration: none;
  border-radius: 4px;
}
#form_builder__edit_link:hover {
  color: #06a4ff;
}

/**
 * The majority of styles for this Template Set are pulled directly from the Core's default theme.
 * This supplements them for a few things that aren't covered.
 */
h1 {
  margin: 0px;
  padding: 28px 0px 0px 21px;
  float: left;
  font-family: 'Lato', Arial;
  color: white;
  font-size: 20px;
  font-weight: normal;
}
#ts_css_nav {
  list-style:none;
  margin: 0px;
  padding: 0px;
}
#ts_css_nav li {
  height: 27px;
}
#ts_css_nav li a, #ts_css_nav li div {
  padding: 2px 0px 2px 4px;
  width: 150px;
}
#ts_css_nav li.completed_page a:link, #ts_css_nav li.completed_page a:visited {
  display: block;
  text-underline: none;
}
#ts_css_nav li.css_nav_current_page div {
  font-weight: bold;
}
.edit_link {
  float: right;
}
.edit_link a:link, .edit_link a:visited {
  padding: 0px 7px;
  background-color: #aaaaaa;
  color: white;
  border-radius: 3px;
  letter-spacing: 0px;
}
.edit_link a:hover {
  background-color: #222222;
  text-decoration: none;
}
#form_builder__edit_link {
  background-color: #444444;
  border-radius: 3px 3px 3px 3px;
  color: white;
  float: right;
  margin: 25px;
  padding: 0 8px;
}
#form_builder__edit_link:hover {
  background-color: #000000;
  text-decoration: none;
}
.ts_heading {
  font: 17.6px/20px Verdana,sans-serif;
  padding-bottom: 5px;
  margin: 0px;
}

data.setting_value:
/* used in the "Highlight" setting for most field types */
.cf_colour_red {
    background-color: #990000;
    color: white;
}
.cf_colour_orange {
    background-color: orange;
}
.cf_colour_yellow {
    background-color: yellow;
}
.cf_colour_green {
    background-color: green;
    color: white;
}
.cf_colour_blue {
    background-color: #336699;
    color: white;
}
/* field comments */
.cf_field_comments {
    font-style: italic;
    color: #999999;
    clear: both;
}

/* column layouts for radios & checkboxes */
.cf_option_list_group_label {
    font-weight: bold;
    clear: both;
    margin-left: 4px;
}
.cf_option_list_2cols,
.cf_option_list_3cols,
.cf_option_list_4cols {
    clear: both;
}
.cf_option_list_2cols .column {
    width: 50%;
    float: left;
}
.cf_option_list_3cols .column {
    width: 33%;
    float: left;
}
.cf_option_list_4cols .column {
    width: 25%;
    float: left;
}

/* Used for the date and time pickers */
.cf_date_group img {
    margin-bottom: -4px;
    padding: 1px;
}
